import React, { useState } from 'react';

interface FormProps {
  initialValues: Joke;
  onClose: () => void;
  onSubmit: (updatedJoke: Joke) => void;
  onDelete: () => void;
}

interface Joke {
  id: number;
  Title: string;
  Body: string;
  Author: string;
  Views: number;
  CreatedAt: number;
}


const Form: React.FC<FormProps> = ({ initialValues, onClose, onSubmit, onDelete }) => {
  const [title, setTitle] = useState(initialValues.title);
  const [author, setAuthor] = useState(initialValues.author);
  const [createdDate, setCreatedDate] = useState(initialValues.createdDate);
  const [views, setViews] = useState(initialValues.views);

  const handleTitleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setTitle(event.target.value);
  };

  const handleAuthorChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setAuthor(event.target.value);
  };

  const handleCreatedDateChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCreatedDate(event.target.value);
  };

  const handleViewsChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setViews(Number(event.target.value));
  };
  const handleSubmit = () => {
    const updatedJoke: Joke = {
      id: initialValues.id,
      Title: formValues.title,
      Body: initialValues.Body,
      Author: formValues.author,
      Views: formValues.views,
      CreatedAt: Date.parse(initialValues.createdDate),
    };

    onSubmit(updatedJoke);
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input type="text" id="title" value={title} onChange={handleTitleChange} />
        </div>
        <div>
          <label htmlFor="author">Author:</label>
          <input type="text" id="author" value={author} onChange={handleAuthorChange} />
        </div>
        <div>
          <label htmlFor="createdDate">Created Date:</label>
          <input type="text" id="createdDate" value={createdDate} onChange={handleCreatedDateChange} />
        </div>
        <div>
          <label htmlFor="views">Views:</label>
          <input type="number" id="views" value={views} onChange={handleViewsChange} />
        </div>
        <div className="form-buttons">
          <button type="submit">Submit</button>
          <button type="button" onClick={onClose}>Close</button>
          <button type="button" onClick={onDelete}>Delete</button>
        </div>
      </form>
    </div>
  );
};

export default Form;
