import React, { useState } from 'react';

interface FormProps {
  initialValues: Joke;
  onClose: () => void;
  onSubmit: (updatedJoke: Joke) => void;
  onDelete: () => void;
}

interface Joke {
  id: number;
  Title: string;
  Body: string;
  Author: string;
  Views: number;
  CreatedAt: number;
}


const Form: React.FC<FormProps> = ({
  initialValues,
  onClose,
  onSubmit,
  onDelete,
}) => {
  const [formValues, setFormValues] = useState<Joke>({
    id: initialValues.id,
    Title: initialValues.Title,
    Body: initialValues.Body,
    Author: initialValues.Author,
    Views: initialValues.Views,
    CreatedAt: initialValues.CreatedAt,
  });

  const handleChange = (
    event: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = event.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  };

  const handleSubmit = () => {
    const updatedJoke: Joke = {
      id: initialValues.id,
      Title: formValues.Title,
      Body: formValues.Body,
      Author: formValues.Author,
      Views: formValues.Views,
      CreatedAt: formValues.CreatedAt,
    };

    onSubmit(updatedJoke);
  };
  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input type="text" id="title" value={title} onChange={handleTitleChange} />
        </div>
        <div>
          <label htmlFor="author">Author:</label>
          <input type="text" id="author" value={author} onChange={handleAuthorChange} />
        </div>
        <div>
          <label htmlFor="createdDate">Created Date:</label>
          <input type="text" id="createdDate" value={createdDate} onChange={handleCreatedDateChange} />
        </div>
        <div>
          <label htmlFor="views">Views:</label>
          <input type="number" id="views" value={views} onChange={handleViewsChange} />
        </div>
        <div className="form-buttons">
          <button type="submit">Submit</button>
          <button type="button" onClick={onClose}>Close</button>
          <button type="button" onClick={onDelete}>Delete</button>
        </div>
      </form>
    </div>
  );
};

export default Form;
