import React, { useEffect, useState } from 'react';
import './Table.css';

interface Joke {
  id: number;
  Title: string;
  Body: string;
  Author: string;
  Views: number;
  CreatedAt: number;
}

const Table: React.FC = () => {
  const [jokes, setJokes] = useState<Joke[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(5);
  const [totalItems, setTotalItems] = useState(0);

  useEffect(() => {
    fetchJokes();
  }, [currentPage, itemsPerPage]);

  const fetchJokes = async () => {
    try {
      const response = await fetch(
        `https://retoolapi.dev/zu9TVE/jokes?_page=${currentPage}&_limit=${itemsPerPage}`
      );
      const data = await response.json();
      setJokes(data);
      setTotalItems(parseInt(response.headers.get('X-Total-Count') || '0', 10));
    } catch (error) {
      console.error('Error fetching jokes:', error);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  const handleNextPage = () => {
    const maxPage = Math.ceil(totalItems / itemsPerPage);
    if (currentPage < maxPage) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const handleItemsPerPageChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setItemsPerPage(parseInt(event.target.value, 10));
    setCurrentPage(1);
  };

  const getViewsColor = (views: number): string => {
    if (views >= 0 && views <= 25) {
      return 'tomato';
    } else if (views >= 26 && views <= 50) {
      return 'orange';
    } else if (views >= 51 && views <= 75) {
      return 'yellow';
    } else if (views >= 76 && views <= 100) {
      return 'green';
    } else {
      return 'inherit';
    }
  };

  return (
    <div>
      <table >
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Created Date</th>
            <th>Views</th>
          </tr>
        </thead>
        <tbody>
          {jokes.map((joke) => (
            <tr key={joke.id}>
              <td>{joke.Title}</td>
              <td>{joke.Author}</td>
              <td>{new Date(joke.CreatedAt).toLocaleDateString()}</td>
              <td style={{ color: getViewsColor(joke.Views) }}>{joke.Views}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <button onClick={handlePrevPage} disabled={currentPage === 1}>{'<'}</button>
        <span>Page {currentPage}</span>
        <button onClick={handleNextPage} disabled={currentPage === Math.ceil(totalItems / itemsPerPage)}>{'>'}</button>
      </div>
      <div>
        <label htmlFor="itemsPerPage">Items per page:</label>
        <select id="itemsPerPage" value={itemsPerPage} onChange={handleItemsPerPageChange}>
          <option value="5">5</option>
          <option value="10">10</option>
        </select>
      </div>
    </div>
  );
};

export default Table
